let unit = {};
if(!toggle) /* First time */
{
    flow.set("co2GasInspect", true);
    unit = {
        groups: 
        { 
            hide: 
            [
                "ncd-co2-gas-sensor:co2-gas-gauges", 
                "ncd-co2-gas-sensor:co2-gas-history",
                "ncd-co2-gas-sensor:co2-gas-battery-percent"
