            [
                "ncd-co2-gas-sensor:co2-gas-download",
                "ncd-co2-gas-sensor:co2-gas-select-mac"
            ]
        } 
    }
    toggle++;
}
else /* Second time */
{
    flow.set("co2GasInspect", false);
    unit = 
    {
